// src/components/AddFacilityForm.js

import React, { useState } from 'react';

const AddFacilityForm = ({ onAddFacility, onCancel }) => {
  const [newFacilityData, setNewFacilityData] = useState({
    name: '',
    type: 'Spor Tesisi',
    capacity: 0,
    available_spots: 0,
    dimensions: { width: 0, length: 0 },
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewFacilityData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSave = () => {
    onAddFacility(newFacilityData);
    onCancel();
  };

  return (
    <div className="modal">
      <h3>Add New Facility</h3>
      <form>
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={newFacilityData.name}
          onChange={handleInputChange}
        />
        <label>Type:</label>
        <select
          name="type"
          value={newFacilityData.type}
          onChange={handleInputChange}
        >
          <option value="Spor Tesisi">Spor Tesisi</option>
          {/* Diğer tip seçeneklerini ekleyebilirsiniz */}
        </select>
        <label>Capacity:</label>
        <input
          type="number"
          name="capacity"
          value={newFacilityData.capacity}
          onChange={handleInputChange}
        />
        <label>Available Spots:</label>
        <input
          type="number"
          name="available_spots"
          value={newFacilityData.available_spots}
          onChange={handleInputChange}
        />
        <button type="button" onClick={handleSave}>Save</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </form>
    </div>
  );
};

export default AddFacilityForm;
