// src/components/MainContent.js

import React, { useState } from 'react';
import facilitiesData from '../data/facilities';
import FacilityCard from './FacilityCard';
import AddFacilityForm from './AddFacilityForm';
import './MainContent.css';

function MainContent() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [newFacilityModal, setNewFacilityModal] = useState(false);
  const [newFacilityData, setNewFacilityData] = useState({
    name: '',
    type: 'Spor Tesisi',
    capacity: 0,
    available_spots: 0,
    dimensions: { width: 0, length: 0 },
  });

  const handleNewFacilityChange = (e) => {
    const { name, value } = e.target;
    setNewFacilityData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleNewFacilityCancel = () => {
    setNewFacilityModal(false);
  };

  const handleNewFacilitySave = () => {
    console.log("New Facility Data:", newFacilityData);

    // Burada yeni tesisi veri kümesine ekleyebilirsiniz (örneğin facilitiesData.push(newFacilityData);)
    // Ancak, bu örnekte sadece console.log ile gösteriyorum.

    setNewFacilityData({
      name: '',
      type: 'Spor Tesisi',
      capacity: 0,
      available_spots: 0,
      dimensions: { width: 0, length: 0 },
    });

    setNewFacilityModal(false);
  };

  const handleSearch = () => {
    const result = facilitiesData.find(facility => facility.name.toLowerCase() === searchTerm.toLowerCase());
    setSearchResult(result);
  };

  return (
    <main>
      <h2>Welcome to the Admin Panel!</h2>

      {/* Kullanıcı Arama */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter a name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Arama Sonuçları */}
      {searchResult && (
        <div className="search-result">
          <h3>Search Result:</h3>
          <FacilityCard facility={searchResult} />
        </div>
      )}

      {/* Tesis Kartları */}
      <h3>Facilities:</h3>
      <div className="facility-list">
        {facilitiesData.map((facility, index) => (
          <FacilityCard key={index} facility={facility} />
        ))}
      </div>

      {/* Yeni Tesisi Ekleme Butonu */}
      <button onClick={() => setNewFacilityModal(true)}>Add New Facility</button>

      {/* Yeni Tesisi Ekleme Formu */}
      {newFacilityModal && (
        <AddFacilityForm
        newFacilityData={newFacilityData}
        onChange={handleNewFacilityChange}
        onSave={handleNewFacilitySave}
        onCancel={handleNewFacilityCancel}
      />

      )}
    </main>
  );
}

export default MainContent;
