// src/components/MainContent.js

import React, { useState } from 'react';
import facilitiesData from '../data/facilities';
import FacilityCard from './FacilityCard';
import AddFacilityForm from './AddFacilityFrom';
import './MainContent.css';

function MainContent() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState(null);


  //facility eklemek için kod


  const handleSearch = () => {
    // Burada gerçek bir veritabanı sorgusu veya başka bir API çağrısı yapabilirsiniz.
    // Ancak, şu anlık basit bir örnek olarak, tesis verileri içinde arama yapacağız.
    const result = facilitiesData.find(facility => facility.name.toLowerCase() === searchTerm.toLowerCase());

    setSearchResult(result);
  };

  const handleNewFacilitySave = () => {
    // Yeni tesisi veri kümesine ekleyebilirsiniz
    // Bu örnekte sadece console.log ile gösteriyorum.
    console.log("New Facility Data:", newFacilityData);

    // Verileri sıfırla
    setNewFacilityData({
      name: '',
      type: 'Spor Tesisi',
      capacity: 0,
      available_spots: 0,
      dimensions: { width: 0, length: 0 },
    });

    setNewFacilityModal(false);
  };

  return (
    <main>
      <h2>Welcome to the Admin Panel!</h2>

      {/* Kullanıcı Arama */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter a name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Arama Sonuçları */}
      {searchResult && (
        <div className="search-result">
          <h3>Search Result:</h3>
          <FacilityCard facility={searchResult} />
        </div>
      )}

      {/* Tesis Kartları */}
      <h3>Facilities:</h3>
      <div className="facility-list">
        {facilitiesData.map((facility, index) => (
          <FacilityCard key={index} facility={facility} />
        ))}



      {/* Yeni Tesisi Ekleme Formu */}
      {newFacilityModal && (
        <AddFacilityForm
          newFacilityData={newFacilityData}
          onChange={handleNewFacilityChange}
          onSave={handleNewFacilitySave}
          onCancel={handleNewFacilityCancel}
        />
      )}
      </div>
    </main>
  );
}

export default MainContent;
