import React, { useState, useEffect } from 'react';
import './Calendar.css'; // Diğer stil dosyalarınızı da burada import edebilirsiniz.

const Calendar = () => {
  const [currentMonth, setCurrentMonth] = useState('');
  const [days, setDays] = useState([]);
  const [selectedDay, setSelectedDay] = useState(null); // Yeni eklenen state
  const [originalColor, setOriginalColor] = useState(null); // Orijinal rengi saklamak için state
  const [allowChangeColor, setAllowChangeColor] = useState(false); // Allow durumu için state

  useEffect(() => {
    const currentDate = new Date();
    const currentMonth = currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
    setCurrentMonth(currentMonth);

    const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
    const startingDay = firstDayOfMonth.getDay(); // 0: Sunday, 1: Monday, ..., 6: Saturday

    const lastDayOfPrevMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 0).getDate();
    const daysInMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).getDate();

    const daysArray = [];

    // Add previous month's days
    for (let i = lastDayOfPrevMonth - startingDay + 1; i <= lastDayOfPrevMonth; i++) {
      daysArray.push(i);
    }

    // Add current month's days
    for (let i = 1; i <= daysInMonth; i++) {
      daysArray.push(i);
    }

    setDays(daysArray);
  }, []);


  const handleDayClick = (day) => {
    console.log('Selected Day:', day);
    setSelectedDay(day);
    setAllowChangeColor(true); // Allow durumunu true yap

    const dayElement = document.getElementById(`day-${day}`);
    if (dayElement) {
      setOriginalColor(dayElement.style.backgroundColor || '');
    }
  };


  const handleChangeBackgroundColor = () => {
    if (selectedDay !== null) {
      const dayElement = document.getElementById(`day-${selectedDay}`);
      if (dayElement) {
        if (dayElement.style.backgroundColor === 'red') {
          dayElement.style.backgroundColor = originalColor;
        } else {
          dayElement.style.backgroundColor = 'red';
        }
      }
    }
  };

  const handleRestrictClick = () => {
    setAllowChangeColor(false); // Allow durumunu false yap
  };

  return (
    <div className="calendar">
      <h2>{currentMonth}</h2>
      <div className="weekdays">
        <div className="weekday">Mon</div>
        <div className="weekday">Tue</div>
        <div className="weekday">Wed</div>
        <div className="weekday">Thu</div>
        <div className="weekday">Fri</div>
        <div className="weekday">Sat</div>
        <div className="weekday">Sun</div>
      </div>

      <div className="days">
        {days.map((day, index) => (
          <div
            key={index}
            id={`day-${day}`}
            className={`day ${selectedDay === day ? 'active' : ''}`}
            onClick={() => handleDayClick(day)}
          >
            {day}
          </div>
        ))}
      </div>
      <p>Selected Day: {selectedDay}</p>
      {allowChangeColor ? (
        <button onClick={handleChangeBackgroundColor}>Change Background Color</button>
      ) : (
        <button onClick={handleRestrictClick}>Allow</button>
      )}
    </div>
  );
};

export default Calendar;
