import React, { useState, useEffect } from 'react';
import './Calendar.css';

const Calendar = () => {
  const [currentMonth, setCurrentMonth] = useState('');
  const [days, setDays] = useState([]);

  const getCurrentMonthInfo = () => {
    const currentDate = new Date();
    const currentMonth = currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
    const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
    let startingDay = firstDayOfMonth.getDay(); // 0: Sunday, 1: Monday, ..., 6: Saturday

    // Adjust startingDay to start from Sunday (0) if it's currently Saturday (6)
    startingDay = startingDay === 0 ? 7 : startingDay;

    return { currentMonth, startingDay };
  };


  useEffect(() => {
    getCurrentMonthInfo();
  }, []);

  return (
    <div className="calendar">
      <h2>{currentMonth}</h2>
      <div className="weekdays">
        <div className="weekday">Mon</div>
        <div className="weekday">Tue</div>
        <div className="weekday">Wed</div>
        <div className="weekday">Thu</div>
        <div className="weekday">Fri</div>
        <div className="weekday">Sat</div>
        <div className="weekday">Sun</div>
      </div>
      <div className="days">
        {days.map((day, index) => (
          <div key={index} className="day">
            {day}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Calendar;
