// src/components/TimeSlots.js

import React, { useState } from 'react';
import './Agenda.css';

const TimeSlots = ({ day, handleSlotClick }) => {
  const generateTimeSlots = () => {
    const timeSlots = [];
    for (let i = 8; i <= 18; i += 2) {
      timeSlots.push({
        slot: `${i}:00 - ${i + 2}:00`,
        isReserved: false,
      });
    }
    return timeSlots;
  };

  const [selectedSlot, setSelectedSlot] = useState(null);

  const handleReserveClick = () => {
    if (selectedSlot) {
      // Burada rezervasyon işlemlerini yapabilirsiniz
      // Örneğin, API'ye rezervasyon isteği göndermek gibi
      console.log(`Reserved: ${selectedSlot}`);
    }
  };

  return (
    <div className="time-slots-card">
      <h4>Time Slots for {day}:</h4>
      <div className="time-slots-container">
        {generateTimeSlots().map((timeSlot, index) => (
          <div
            key={index}
            onClick={() => setSelectedSlot(timeSlot.slot)}
            className={`time-slot-card ${timeSlot.isReserved ? 'reserved' : ''} ${selectedSlot === timeSlot.slot ? 'selected' : ''}`}
          >
            {timeSlot.slot}
          </div>
        ))}
      </div>
      {selectedSlot && (
        <button className="reserve-button" onClick={handleReserveClick}>
          Reserve {selectedSlot}
        </button>
      )}
    </div>
  );
};

export default TimeSlots;
