import React, { useState } from 'react';
import initialFacilitiesData from '../data/facilities';
import FacilityCard from './FacilityCard';
import Calendar from './Calendar';  // Yeni eklenen bileşen
import './Calendar.css'; // Diğer stil dosyalarınızı da burada import edebilirsiniz.
import './MainContent.css';

function MainContent() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [newFacilityModal, setNewFacilityModal] = useState(false);
  const [facilitiesData, setFacilitiesData] = useState(initialFacilitiesData);
  const [newFacilityData, setNewFacilityData] = useState({
    name: '',
    type: '',
    capacity: 0,
    available_spots: 0,
    dimensions: { width: 0, length: 0 },
  });

  const handleSearch = () => {
    const result = facilitiesData.find(facility => facility.name.toLowerCase() === searchTerm.toLowerCase());
    setSearchResult(result);
  };

  const handleNewFacilityChange = (e) => {
    const { name, value } = e.target;
    setNewFacilityData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleNewFacilityCancel = () => {
    setNewFacilityModal(false);
  };

  const handleNewFacilitySave = () => {
    console.log("New Facility Data:", newFacilityData);

    // Update facilitiesData with the new facility
    setFacilitiesData((prevData) => [...prevData, newFacilityData]);

    // Reset the form data and close the modal
    setNewFacilityData({
      name: '',
      type: 'Spor Tesisi',
      capacity: 0,
      available_spots: 0,
      dimensions: { width: 0, length: 0 },
    });

    setNewFacilityModal(false);
  };

  const getCurrentMonthInfo = () => {
    const currentDate = new Date();
    const currentMonth = currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
    const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
    const startingDay = firstDayOfMonth.getDay(); // 0: Sunday, 1: Monday, ..., 6: Saturday

    return { currentMonth, startingDay: (startingDay === 0 ? 7 : startingDay) };
  };

  const { currentMonth, startingDay } = getCurrentMonthInfo();

  const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  const daysInMonth = Array.from({ length: 31 }, (_, index) => index + 1);
  const daysToShow = daysInMonth.slice(startingDay);

  return (
    <main>
      <h2>Welcome to the Admin Panel!</h2>

      {/* Kullanıcı Arama */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter a name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Arama Sonuçları */}
      {searchResult && (
        <div className="search-result">
          <h3>Search Result:</h3>
          <FacilityCard facility={searchResult} />
        </div>
      )}

      {/* Tesis Kartları */}
      <h3>Facilities:</h3>
      <div className="facility-list">
        {facilitiesData.map((facility, index) => (
          <FacilityCard key={index} facility={facility} />
        ))}
      </div>

      {/* Yeni Tesisi Ekleme Butonu */}
      <button onClick={() => setNewFacilityModal(true)}>Add New Facility</button>

      {/* Yeni Tesisi Ekleme Formu */}
      {newFacilityModal && (
        <div className="modal">
          <h3>Add New Facility</h3>
          <form>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={newFacilityData.name}
              onChange={handleNewFacilityChange}
            />
            <label>Type:</label>
            <select
              name="type"
              value={newFacilityData.type}
              onChange={handleNewFacilityChange}
            >
              <option value="Spor Tesisi">Spor Tesisi</option>
              {/* Diğer tip seçeneklerini ekleyebilirsiniz */}
            </select>
            <label>Capacity:</label>
            <input
              type="number"
              name="capacity"
              value={newFacilityData.capacity}
              onChange={handleNewFacilityChange}
            />
            <label>Available Spots:</label>
            <input
              type="number"
              name="available_spots"
              value={newFacilityData.available_spots}
              onChange={handleNewFacilityChange}
            />
            <button type="button" onClick={handleNewFacilitySave}>Save</button>
            <button type="button" onClick={handleNewFacilityCancel}>Cancel</button>
          </form>
        </div>
      )}
      {/* Takvim */}
      <h3>{currentMonth}</h3>
      <div className="calendar">
        {weekDays.map((weekday) => (
          <div key={weekday} className="weekday">
            {weekday}
          </div>
        ))}
        {daysToShow.map((day, index) => (
          <div key={index} className="day">
            {day}
          </div>
        ))}
      </div>
    </main>
  );
}

export default MainContent;
