// src/components/UserCard.js

import React from 'react';
import './UserCard.css';

const UserCard = ({ user }) => {
  const handleButtonClick = () => {
    // Burada gerekli işlemleri gerçekleştirebilirsiniz.
    // Örneğin, eğer restricted değeri true ise bir şeyleri "allow", false ise "restrict" yapabilirsiniz.
    console.log(user.restricted ? 'Allow' : 'Restrict');
  };

  return (
    <div className="user-card">
      <p className="user-id">ID: {user.id}</p>
      <p className="user-info">Name: {user.name}</p>
      <button onClick={handleButtonClick}>
        {user.restricted ? 'Allow' : 'Restrict'}
      </button>
    </div>
  );
};

export default UserCard;
